import React, { useEffect, useState } from "react";
import { jwtDecode } from "jwt-decode";

const Dashboard = (req, res) => {
  const [user, setUser] = useState({});
  const [quote, setQuote] = useState("");
  const [tempQuote, setTempQuote] = "";

  const populateQuote = async () => {
    const response = await fetch("http://localhost:6001/api/quote", {
      method: "GET",
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    });
    const data = await response.json();
    setQuote(data.quote);
  };

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      const decoded = jwtDecode(token);
      setUser(decoded);
      if (!user) {
        localStorage.removeItem("token");
      } else {
        populateQuote();
      }
    }
  }, []);

  const handleUpdateQuote = async ()=>{
    const response = await fetch('http://localhost:6001/api/update-quote', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: json.strin
    });
  }

  return (
    <>
      <h1>Dashboard</h1>
      <h3>Welcome, {user.name}</h3>
      <p>Your Quote is: {quote ? quote : "No Quote"}</p>
      <form action="">
        <input
          type="text"
          placeholder="Enter Quote"
          onChange={(e) => setTempQuote(e.target.value)}
          value={tempQuote}
        />
        <button onClick={handleUpdateQuote}>Update Quote</button>
      </form>
    </>
  );
};

export default Dashboard;
